namespace models\email;

/*
 * Emailtemplate class
 *
 * Hier worden alle html styles voor email bewaard
 *
 */
class EmailHtmlTemplates
{
	private $_html = NULL;
	private $_logo__uitzenden_base64 = '';
	private $_logo_bemiddeling_base64 = '';

	/**----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
	/*
	 * constructor stelt juiste template in
	 *
	 *
	 * @param string
	 * @return object
	 */
	public function __construct( $template = '' )
	{
		$method = '_' . $template;

		if( method_exists( $this, $method ) )
		{
			$this->$method();
		}

		return $this;
	}

	/**----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
	/*
	 * geeft de juiste html terug
	 *
	 *
	 * @return string
	 */
	public function html()
	{
		return $this->_html;
	}
	
	/**----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
	/*
	 * Template voor mails buiten de app, vanuit devis
	 *
	 * @return void
	 */
	public function _devis()
	{
		
		//head
		$this->_html = '<!doctype html><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=UTF-8">';
		
		//css
		$this->_html .= '<style type="text/css">
							#outlook a { padding: 0; }
						 	.ReadMsgBody { width: 100%; }
							.ExternalClass { width: 100%; }
							.ExternalClass * { line-height:100%; }
       
							body{background-color: #F2F2F2; margin: 0; padding: 0; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100%; text-align: center; font-family:Verdana, Arial, sans-serif; font-size: 12px }
							.header{background-color: #002E65; width: 685px; color:#fff; text-align: left; font-size: 20px; padding-top:15px;  padding-bottom:15px; display: block; padding-left: 25px}
							.footer{background-color: #002E65; width: 685px; height: 25px; color:#fff; text-align: center; font-size: 11px;}
							.footer a, footer a:visited{ color:#fff !important;}
							.text-container{ display: block;  background-color: #FFFFFF; width: 700px; text-align: left; border-width: 0px; border-collapse: collapse}
							.text-container td.titel{padding:25px 25px 0 25px; font-size: 15px}
							.text-container td.text{padding: 25px; line-height: 24px; color: #999999}
						 </style>';
		
		//close head
		$this->_html .= '</head>';
		
		//body start
		$this->_html .= '<body>';
		
		//blauw header
		$this->_html .= '';
		
		$this->_html .= '<table class="text-container">';
		$this->_html .= '<tr><td class="header">Devis Online</td></tr>';
		
		//titel var
		$this->_html .= '<tr><td class="titel">';
		$this->_html .= '{{titel}}';
		$this->_html .= '</td></tr>';
		
		//var in de body
		$this->_html .= '<tr><td class="text">';
		$this->_html .= '{{body}}';
		$this->_html .= '</td></tr>';
		
		$this->_html .= '<tr><td class="footer">Devils Online</td></tr>';
		
		$this->_html .= '</table>';
		
		
		//close body
		$this->_html .= '</body></html>';
	}
	

	/**----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
	/*
	 * Template voor mails vanuit de app
	 *
	 * @return void
	 */
	public function _default()
	{
		$CI =& get_instance();
		$bedrijfsgegevens = $CI->werkgever->bedrijfsgegevens();
		
		//head
		$this->_html = '<!doctype html><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=UTF-8">';

		//css
		$this->_html .= '<style type="text/css">
							#outlook a { padding: 0; }
						 	.ReadMsgBody { width: 100%; }
							.ExternalClass { width: 100%; }
							.ExternalClass * { line-height:100%; }
        
							body{background-color: #F2F2F2; margin: 0; padding: 0; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100%; text-align: center; font-family:Verdana, Arial, sans-serif; font-size: 12px }
							.header-white{background-color: #ffffff; width: 685px; color:#666666; text-align: center; font-size:30px; font-weight: bold; padding-top:15px;  padding-bottom:15px; display: block; padding-left: 25px; line-height: 16px}
							.header-white img{ width: 260px}
							.header-white-sub{background-color: #ffffff; width: 685px; color:#666666; text-align: center; font-size: 14px; padding-top:10px;  padding-bottom:35px; display: block; padding-left: 25px}
							.header-blue{background-color: #2DA4DC; width: 685px; color:#fff; text-align: center; font-size: 20px; padding-top:15px;  padding-bottom:15px; display: block; padding-left: 25px}
							.footer{background-color: #fffff; width: 685px; height: 25px; color:#fff; text-align: center; font-size: 11px;}
							.footer a, footer a:visited{ color:#fff !important;}
							.text-container{ display: block;  background-color: #FFFFFF; width: 700px; text-align: left; border-width: 0px; border-collapse: collapse}
							td.titel{padding:25px 25px 0 25px; font-size: 15px; background-color: #ffffff}
							td.text{padding: 25px; line-height: 24px; background-color: #ffffff; color: #555555}
						 </style>';
		
		//close head
		$this->_html .= '</head>';

		//body start
		$this->_html .= '<body>';
		
		//alles centreren
		$this->_html .= '<table width="100%" border="0" cellspacing="0" cellpadding="0" style="padding-top: 25px"><tr><td align="center">';
		
		
		$this->_html .= '';
		$this->_html .= '<table class="text-container">';
		
		//witte header
		if( $bedrijfsgegevens['bedrijfsnaam'] == 'FlexxOffice Uitzend B.V.' )
		{
			$this->_html .= '<tr><td class="header-white">';
			$this->_html .= '<img src="data:image/jpeg;base64,' . $this->_logo__uitzenden_base64 . '">';
			$this->_html .= '</td></tr>';
		}
		elseif( $bedrijfsgegevens['bedrijfsnaam'] == 'FlexxOffice Bemiddeling B.V.' )
		{
			$this->_html .= '<tr><td class="header-white">';
			$this->_html .= '<img src="data:image/jpeg;base64,' . $this->_logo_bemiddeling_base64 . '">';
			$this->_html .= '</td></tr>';
		}
		else
		{
			$this->_html .= '<tr><td class="header-white">'.$bedrijfsgegevens['bedrijfsnaam'].'</td></tr>';
		}
		
		$this->_html .= '<tr><td class="header-white-sub">';
		$this->_html .= '<span style="color:#FFFFFF">{{titel}}</span>';
		$this->_html .= 'FLEXIBEL&nbsp;&nbsp;&nbsp;|&nbsp;&nbsp;&nbsp;DESKUNDIG&nbsp;&nbsp;&nbsp;|&nbsp;&nbsp;&nbsp;INNOVATIEF&nbsp;&nbsp;&nbsp;|&nbsp;&nbsp;&nbsp;BETROUWBAAR';
		$this->_html .= '</td></tr>';
		
		//blauw header
		$this->_html .= '<tr><td class="header-blue">{{titel}}</td></tr>';
		
		//titel var
		$this->_html .= '<tr><td class="titel">';
		$this->_html .= '';
		$this->_html .= '</td></tr>';
		
		//var in de body
		$this->_html .= '<tr><td class="text">';
		$this->_html .= '{{body}}';
		$this->_html .= '</td></tr>';
		
		$this->_html .= '<tr><td class="text">';
		
		$this->_html .= '<table style="width: 100%; color:#555555"><tr><td align="left">';
		$this->_html .= '<span style="font-size: 15px; font-weight: bold">' . $bedrijfsgegevens['bedrijfsnaam'].'</span><br />'.$bedrijfsgegevens['straat'].' '.$bedrijfsgegevens['huisnummer'] . '<br />' . $bedrijfsgegevens['postcode'].' '.$bedrijfsgegevens['plaats'];
		$this->_html .= '</td><td align="right" style="padding-top: 28px;">';
		$this->_html .= '<span>' . $bedrijfsgegevens['telefoon'].'<br /> '.$bedrijfsgegevens['email'].' </span>';
		$this->_html .= '</td></tr>';
		
		$this->_html .= '</td></tr>';
		
		/*$this->_html .= '<tr><td class="footer">'.$bedrijfsgegevens['straat'].' '.$bedrijfsgegevens['huisnummer'].' | '.$bedrijfsgegevens['postcode'].' '.$bedrijfsgegevens['plaats'].'|
		'.$bedrijfsgegevens['telefoon'].' | '.$bedrijfsgegevens['email'].'</td></tr>';*/
		
		$this->_html .= '</table>';
		
		//einde centreren
		$this->_html .= '</td></tr></table>';
		//close body
		$this->_html .= '</body></html>';
	}

}